import "./App.css";
import axios from "axios";
import { useState, useEffect } from "react";
import styled from "styled-components";

function App() {
  const [data, setData] = useState();
  const [columns, setColumns] = useState(5); // 초기 컬럼 수 (기본적으로 5)

  const getPhotos = async () => {
    try {
      const res = await axios.get(
        "https://picsum.photos/v2/list?page=2&limit=100​​"
      );
      setData(res.data);
    } catch (err) {
      console.log(err);
    }
  };
  useEffect(() => {
    getPhotos();

    const handleResize = () => {
      const screenWidth = window.innerWidth;
      if (screenWidth >= 1008) {
        setColumns(5); // 화면 너비가 1024px 이상이면 5개의 컬럼
      } else if (screenWidth >= 860) {
        setColumns(4); // 화면 너비가 860px 이상이면 4개의 컬럼
      } else if (screenWidth >= 360) {
        setColumns(3); // 화면 너비가 360px 이상이면 3개의 컬럼
      }
    };

    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  return (
    <>
      <Grid>
        {data && data.map((el, id) => <Img key={id} src={el.download_url} />)}
      </Grid>
    </>
  );
}

const Grid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr 1fr;
`;

const Img = styled.img`
  width: 15rem;
  border-radius: 10px;
`;

export default App;
