import styled from "styled-components";
import { useRecoilState } from "recoil";
import { ImgAtom } from "../recoil/ImgAtom";
import Profile from "../components/Profile";
import edit from "../assets/editIcon.png";
import { useState } from "react";
import EditModal from "../components/EditModal";

const MyPage = () => {
  const [savedImages, setSavedImages] = useRecoilState(ImgAtom); // 이미지 저장, 삭제 atom
  const [hoveredIndex, setHoveredIndex] = useState(-1); // hover된 이미지 id
  const [isModal, setIsModal] = useState(false);

  return (
    <Container>
      <Center>
        <Profile />
        <Columns>
          {savedImages.map((el) => (
            <Figure
              key={el.id}
              onMouseEnter={() => setHoveredIndex(el.id)}
              onMouseLeave={() => setHoveredIndex(-1)}
            >
              {hoveredIndex === el.id && (
                <EditIcon
                  src={edit}
                  onClick={() => {
                    setIsModal(true);
                  }}
                />
              )}
              <Img
                key={el.id}
                src={el.download_url}
                hover={hoveredIndex === el.id ? 1 : 0}
              />
            </Figure>
          ))}
        </Columns>
      </Center>
    </Container>
  );
};

const EditIcon = styled.img`
  position: absolute;
  z-index: 2;
  width: 2rem;
  margin-left: 1rem;
  margin-top: 1rem;
  cursor: pointer;
`;

const Columns = styled.div`
  max-width: 1200px;
  column-width: 210px;
  column-gap: 15px;
  margin-left: 6%;
  margin-right: 6%;
`;

const Figure = styled.div`
  display: inline-block;
  margin: 0;
  margin-bottom: 30px;
`;

const Container = styled.div`
  display: flex;
  justify-content: center;
`;

const Center = styled.div`
  margin-top: 5.4rem;
`;

const Img = styled.img`
  width: 100%;
  border-radius: 18px;
  cursor: pointer;

  transition: filter 100ms ease;

  filter: ${(props) =>
    props.hover === 1 ? "brightness(55%)" : "brightness(100%)"};
`;

export default MyPage;
