import axios from "axios";
import { useState, useEffect } from "react";
import styled from "styled-components";
import { ImgAtom } from "../recoil/ImgAtom";

const MainPage = () => {
  const [data, setData] = useState([]);
  const [columns, setColumns] = useState(5); // 초기 컬럼 수 (기본적으로 5)
  const [hoveredIndex, setHoveredIndex] = useState(-1);
  const [savedImages, setSavedImages] = useState([]);

  const getPhotos = async () => {
    try {
      const res = await axios.get(
        "https://picsum.photos/v2/list?page=2&limit=100​​"
      );
      setData(res.data);
      console.log(res.data);
    } catch (err) {
      console.log(err);
    }
  };
  useEffect(() => {
    getPhotos();

    const handleResize = () => {
      const screenWidth = window.innerWidth;
      if (screenWidth >= 1008) {
        setColumns(5);
      } else if (screenWidth >= 841) {
        setColumns(4);
      } else if (screenWidth >= 520) {
        setColumns(3);
      } else {
        setColumns(2);
      }
    };

    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  const handleSaveClick = (id) => {
    // 이미 클릭된 이미지인지 확인
    if (savedImages.includes(id)) {
      // 이미 클릭된 이미지인 경우, 제거
      setSavedImages(savedImages.filter((imageId) => imageId !== id));
    } else {
      // 클릭되지 않은 경우, 추가
      setSavedImages([...savedImages, id]);
      alert("The photo has been saved!");
    }
  };

  return (
    <Container>
      <Columns>
        {data &&
          data.map((el, id) => (
            <Figure
              columns={columns}
              key={id}
              onMouseEnter={() => setHoveredIndex(id)}
              onMouseLeave={() => setHoveredIndex(-1)}
            >
              {hoveredIndex === id && (
                <SaveBtn
                  save={savedImages.includes(id) ? 1 : 0}
                  onClick={() => {
                    handleSaveClick(id);
                  }}
                >
                  {savedImages.includes(id) ? "Saved" : "Save"}
                </SaveBtn>
              )}
              <Img
                key={id}
                src={el.download_url}
                hover={hoveredIndex === id ? 1 : 0}
              />
              <AuthorContainer>
                <Profile>{el.author.slice(0, 1)}</Profile>
                <Author>{el.author}</Author>
              </AuthorContainer>
            </Figure>
          ))}
      </Columns>
    </Container>
  );
};

const SaveBtn = styled.button`
  position: absolute;
  z-index: 2;
  margin-left: 1rem;
  margin-top: 0.8rem;
  background-color: ${(props) => (props.save === 1 ? "black" : "#e60023")};
  color: white;
  border: none;
  border-radius: 30px;
  font-weight: 600;
  padding: 1rem;
  font-size: 15px;
  cursor: pointer;
`;

const Profile = styled.button`
  width: 2rem;
  height: 2rem;
  background-color: #efefef;
  border-radius: 100%;
  border: none;
  margin-right: 0.4rem;
`;

const AuthorContainer = styled.div`
  display: flex;
  align-items: center;
  margin-top: 0.3rem;
`;

const Author = styled.p`
  margin: 0;
  font-size: 14px;
`;

const Container = styled.div`
  display: flex;
  justify-content: center;
`;

const Columns = styled.div`
  max-width: 1200px;
  column-width: 210px;
  column-gap: 15px;
  margin-left: 6%;
  margin-right: 6%;
  margin-top: 5.2rem;
`;

const Figure = styled.div`
  display: inline-block;
  margin: 0;
  margin-bottom: 30px;
`;

const Img = styled.img`
  width: 100%;
  border-radius: 18px;
  cursor: pointer;
  transition: filter 100ms ease;

  filter: ${(props) =>
    props.hover === 1 ? "brightness(55%)" : "brightness(100%)"};
`;

export default MainPage;
