import { atom } from "recoil";

// export const ImgAtom = atom({
//   key: "ImgAtom",
//   default: [],
// });

interface ImageInfo {
  id: number;
  download_url: string;
  author: string;
}

export const ImgAtom = atom<ImageInfo[]>({
  key: "ImgAtom",
  default: [], // 초기값은 빈 배열
});
