import axios from "axios";
import { useState, useEffect } from "react";
import styled from "styled-components";
import { useRecoilState } from "recoil";
import { ImgAtom } from "../recoil/ImgAtom";
import Profile from "../components/common/Profile";

const MyPage = () => {
  const [data, setData] = useState([]); // 이미지 데이터
  const [savedImages, setSavedImages] = useRecoilState(ImgAtom); // 이미지 저장, 삭제 atom

  const getPhotos = async () => {
    try {
      const res = await axios.get(
        "https://picsum.photos/v2/list?page=2&limit=100​​"
      );
      setData(res.data.filter((el) => savedImages.includes(el.id)));
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    getPhotos();
  }, []);

  return (
    <Container>
      <Center>
        <Profile />

        {data && data.map((el) => <Img src={el.download_url} />)}
      </Center>
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  justify-content: center;
`;

const Center = styled.div`
  margin-top: 5.4rem;
`;

const Img = styled.img`
  width: 14rem;
  border-radius: 18px;
`;

export default MyPage;
